    def test_set_connection(self):
        e = self.explore_eth.provider_url
        print(e)
        self.assertEqual(self.explore_eth.set_connection().isConnected(), True)
        self.explore_eth.use_test_net = True
        self.assertEqual(self.explore_eth.set_connection().isConnected(), True)
        self.assertEqual(self.explore_bsc.set_connection().isConnected(), True)
        self.assertEqual(self.explore_polygon.set_connection().isConnected(), True)

    def test_check_balance(self):

        contract = "0x229b1b6C23ff8953D663C4cBB519717e323a0a84"
        wallet_address = "0x96167d79e03A37d114FEDb14bD9DeCA2a49ea870"

        loop = asyncio.get_event_loop()
        def h():
            print("KKKJ")

        task = loop.create_task(
            self.explore_polygon.check_balance(contract=contract, abi=self.blok_abi,
                                               wallet_address=wallet_address,
                                               poll_interval=2, old_balance=5405371433.0, handle=h)
            )

        loop.run_until_complete(task)

    def test_convert_from_hex(self):
        num = "0x00000000000000000000000000000000000000000000000000000004a817c800"
        num = self.explore_eth.convert_from_hex(num)
        self.assertEqual(num, 20000)

    def test_convert_to_checksum_address(self):
        address = b'\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\xfe\xa8V\x91/ \xbcO|\x87|R\xd6\n,\xdcy|n\xf8'
        address = self.explore_eth.convert_to_checksum_address(address)
        self.assertEqual(address.lower(), "0xfea856912f20bc4f7c877c52d60a2cdc797c6ef8")

    def test_custom_filter(self):
        val = self.explore_polygon.custom_filter(from_block=31963060, to_block=31963066,
                                                 address="0x96167d79e03A37d114FEDb14bD9DeCA2a49ea870")
        for v in val.get_all_entries():
            print(v)

